  workouts_30: { id: "workouts_30", title: "M√™s √âpico", description: "Complete 30 dias de treino", icon: "award" as const, rarity: "legendary" as const },
  first_pr: { id: "first_pr", title: "PR Conquistado", description: "Estabele√ßa um novo recorde pessoal", icon: "zap" as const, rarity: "epic" as const },
  novice: { id: "novice", title: "Novato", description: "Complete 3 dias de treino", icon: "star" as const, rarity: "common" as const },
  week_warrior: { id: "week_warrior", title: "1¬™ Semana", description: "Complete uma semana completa", icon: "trophy" as const, rarity: "rare" as const },
  dedicated: { id: "dedicated", title: "Dedica√ß√£o Total", description: "Complete 100 treinos", icon: "heart" as const, rarity: "legendary" as const },
};

export default function Evolution() {
  const { toast } = useToast();

  const { data: stats, isLoading: statsLoading, error: statsError } = useQuery<UserStats>({
    queryKey: ["/api", "user", "stats"],
  });

  const { data: personalRecords = [], isLoading: prsLoading, error: prsError } = useQuery<PersonalRecord[]>({
    queryKey: ["/api", "personal-records"],
  });

  const { data: userBadges = [], isLoading: badgesLoading, error: badgesError } = useQuery<UserBadge[]>({
    queryKey: ["/api", "badges"],
  });

  useEffect(() => {
    if (statsError) {
      toast({
        title: "Erro ao carregar estat√≠sticas",
        description: "N√£o foi poss√≠vel carregar suas estat√≠sticas.",
        variant: "destructive",
      });
    }
  }, [statsError, toast]);

  useEffect(() => {
    if (prsError) {
      toast({
        title: "Erro ao carregar recordes",
        description: "N√£o foi poss√≠vel carregar seus recordes pessoais.",
        variant: "destructive",
      });
    }
  }, [prsError, toast]);

  useEffect(() => {
    if (badgesError) {
      toast({
        title: "Erro ao carregar badges",
        description: "N√£o foi poss√≠vel carregar suas conquistas.",
        variant: "destructive",
      });
    }
  }, [badgesError, toast]);

  const heatmapData = Array.from({ length: 84 }, (_, i) => ({
    date: new Date(Date.now() - (83 - i) * 86400000).toISOString().split("T")[0],
    count: Math.random() > 0.3 ? Math.floor(Math.random() * 3) + 1 : 0,
  }));

  const rpeData = [
    { rpe: "1-2", count: 12 },
    { rpe: "3-4", count: 45 },
    { rpe: "5-6", count: 78 },
    { rpe: "7-8", count: 52 },
    { rpe: "9-10", count: 8 },
  ];

  const sleepPerformanceData = Array.from({ length: 30 }, (_, i) => ({
    sleep: 5 + Math.random() * 4,
    performance: 60 + Math.random() * 40,
  }));

  const earnedBadgeIds = new Set(userBadges.map(ub => ub.badgeId));
  const badges = Object.values(BADGE_DEFINITIONS).map(badge => ({
    ...badge,
    earned: earnedBadgeIds.has(badge.id),
