import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Checkbox } from "@/components/ui/checkbox";
import { Progress } from "@/components/ui/progress";
import { Dumbbell, ArrowLeft, ArrowRight } from "lucide-react";
import { useLocation } from "wouter";
import { useAuth } from "@/lib/auth";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function Onboarding() {
  const [, setLocation] = useLocation();
  const { register, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const [step, setStep] = useState(1);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    email: "",
    name: "",
    age: "",
    sex: "male",
    height: "",
    weight: "",
    experience: "beginner",
    goal: "hypertrophy",
    daysPerWeek: "3",
    sessionMinutes: "45",
    location: "both",
    equipment: [] as string[],
    injuries: "",
  });

  const totalSteps = 5;
  const progress = (step / totalSteps) * 100;

  const handleNext = async () => {
    if (step < totalSteps) {
      setStep(step + 1);
    } else {
      setIsSubmitting(true);
      try {
        const userData = {
          username: formData.username,
          password: formData.password,
          email: formData.email,
          name: formData.name,
          age: parseInt(formData.age),
          sex: formData.sex,
          height: parseInt(formData.height),
          weight: parseFloat(formData.weight),
          experience: formData.experience,
          goal: formData.goal,
          daysPerWeek: parseInt(formData.daysPerWeek),
          sessionMinutes: parseInt(formData.sessionMinutes),
          location: formData.location,
          equipment: formData.equipment,
          injuries: formData.injuries,
        };

        await register(userData);

        toast({
          title: "Gerando seu plano de treino...",
          description: "Aguarde enquanto a IA cria um programa personalizado para voc√™",
        });

        await apiRequest("POST", "/api/workouts/generate-plan", {});

        toast({
          title: "Plano criado com sucesso!",
          description: "Seu treino personalizado est√° pronto",
        });

        setLocation("/dashboard");
      } catch (error: any) {
        toast({
          title: "Erro ao completar cadastro",
          description: error.message || "Tente novamente",
          variant: "destructive",
        });
      } finally {
        setIsSubmitting(false);
      }
    }
  };

  const handleBack = () => {
    if (step > 1) setStep(step - 1);
  };

  const updateFormData = (field: string, value: any) => {
    setFormData({ ...formData, [field]: value });
  };

  return (
    <div className="min-h-screen bg-background flex flex-col">
      <nav className="border-b border-border">
        <div className="max-w-3xl mx-auto px-4 py-4 flex items-center gap-2">
          <Dumbbell className="w-6 h-6 text-primary" />
          <span className="text-xl font-bold font-['Outfit']">FitCoach AI</span>
        </div>
      </nav>

      <div className="flex-1 flex items-center justify-center p-4">
        <div className="w-full max-w-2xl space-y-8">
          <div className="space-y-4">
            <div className="flex items-center justify-between text-sm text-muted-foreground">
              <span>Passo {step} de {totalSteps}</span>
              <span>{Math.round(progress)}%</span>
            </div>
            <Progress value={progress} className="h-2" />
          </div>

          <Card className="p-8">
            {step === 1 && (
              <div className="space-y-6">
                <div>
                  <h2 className="text-2xl font-bold font-['Outfit'] mb-2">Vamos come√ßar!</h2>
                  <p className="text-muted-foreground">Conte um pouco sobre voc√™</p>
                </div>
                <div className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="username">Nome de usu√°rio</Label>
                    <Input
                      id="username"
                      placeholder="seu_usuario"
                      value={formData.username}
                      onChange={(e) => updateFormData("username", e.target.value)}
                      data-testid="input-username"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="password">Senha</Label>
                    <Input
                      id="password"
                      type="password"
                      placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                      value={formData.password}
                      onChange={(e) => updateFormData("password", e.target.value)}
                      data-testid="input-password"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="email">Email</Label>
                    <Input
                      id="email"
                      type="email"
                      placeholder="seu@email.com"
                      value={formData.email}
                      onChange={(e) => updateFormData("email", e.target.value)}
                      data-testid="input-email"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="name">Nome</Label>
                    <Input
                      id="name"
                      placeholder="Seu nome completo"
                      value={formData.name}
                      onChange={(e) => updateFormData("name", e.target.value)}
                      data-testid="input-name"
                    />
                  </div>
                  <div className="grid grid-cols-3 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="age">Idade</Label>
                      <Input
                        id="age"
                        type="number"
                        placeholder="25"
                        value={formData.age}
                        onChange={(e) => updateFormData("age", e.target.value)}
                        data-testid="input-age"
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="height">Altura (cm)</Label>
                      <Input
                        id="height"
                        type="number"
                        placeholder="175"
                        value={formData.height}
                        onChange={(e) => updateFormData("height", e.target.value)}
                        data-testid="input-height"
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="weight">Peso (kg)</Label>
                      <Input
                        id="weight"
                        type="number"
                        placeholder="75"
                        value={formData.weight}
                        onChange={(e) => updateFormData("weight", e.target.value)}
                        data-testid="input-weight"
                      />
                    </div>
                  </div>
                  <div className="space-y-2">
                    <Label>Sexo</Label>
                    <RadioGroup
                      value={formData.sex}
                      onValueChange={(value) => updateFormData("sex", value)}
                    >
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="male" id="male" data-testid="radio-male" />
                        <Label htmlFor="male" className="font-normal cursor-pointer">Masculino</Label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="female" id="female" data-testid="radio-female" />
                        <Label htmlFor="female" className="font-normal cursor-pointer">Feminino</Label>
                      </div>
                    </RadioGroup>
                  </div>
                </div>
              </div>
            )}

            {step === 2 && (
              <div className="space-y-6">
                <div>
                  <h2 className="text-2xl font-bold font-['Outfit'] mb-2">Seu objetivo</h2>
                  <p className="text-muted-foreground">O que voc√™ quer alcan√ßar?</p>
                </div>
                <RadioGroup
                  value={formData.goal}
                  onValueChange={(value) => updateFormData("goal", value)}
                  className="space-y-3"
                >
                  {[
                    { value: "fat_loss", label: "Perda de gordura", description: "Defini√ß√£o e emagrecimento" },
                    { value: "hypertrophy", label: "Ganho de massa muscular", description: "Hipertrofia e crescimento" },
                    { value: "strength", label: "For√ßa", description: "Aumentar carga e performance" },
                    { value: "conditioning", label: "Condicionamento", description: "Resist√™ncia e sa√∫de geral" },
                  ].map((option) => (
                    <Card
                      key={option.value}
                      className={`p-4 cursor-pointer hover-elevate ${
                        formData.goal === option.value ? "border-primary" : ""
